jQuery(document).ready(function(){jQuery("a[href*=#]").click(function(){if((location.pathname.replace(/^\//,"")==this.pathname.replace(/^\//,""))&&(location.hostname==this.hostname)){var a=jQuery(this.hash);a=(a.length&&a)||jQuery("[name="+this.hash.slice(1)+"]");if(a.length){var b=a.offset().top;jQuery("html,body").animate({scrollTop:b},700);return true}}})})